const longestPalindrome = require('./index');

test('should be a function', () => {
  expect(typeof longestPalindrome).toEqual('function');
});

test('should return the correct result', () => {
  const result = longestPalindrome('babad');
  expect(result).toEqual('bab');
});

test('should return the correct result', () => {
  const result = longestPalindrome('cbbd');
  expect(result).toEqual('bb');
});

test('should return the correct result', () => {
  const result = longestPalindrome('a');
  expect(result).toEqual('a');
});

test('should return the correct result', () => {
  const result = longestPalindrome('ac');
  expect(result).toEqual('a');
});

test('should return the correct result', () => {
  const result = longestPalindrome(
    ''
  );
  expect(result).toEqual('gykrkyg');
});
